# Definir las listas para poder clasificar lo huevos y saber cuantos tenemos de cada uno (listas arrancan en 0)
huevos_A=0
huevos_AA=0
huevos_AAA=0
huevos_BC=0

#Definimos la funcion (for) para que me recorra la lista ya obtenida o ingresada anteriormente
for i in range(0,len(lista_huevos)):
        if lista_huevos[i]>=53 and lista_huevos[i]<60:
            huevos_A+=1
        if lista_huevos[i]>=60 and lista_huevos[i]<67:
            huevos_AA+=1
        if lista_huevos[i]>=67:
            huevos_AAA+=1
        if lista_huevos[i]>0 and lista_huevos[i]<53:
            huevos_BC+=1

# Definimos las funciones para cada una de las bandejas, asi las podremos clasificar con su respectiva operacion
def calcular_bandejas_A(huevos_A):
    if huevos_A!=0:
        bandeja_A=huevos_A//30
    return bandeja_A
def calcular_bandejas_AA(huevos_AA):
    if huevos_AA!=0:
        bandeja_AA=huevos_AA//24
    return bandeja_AA
def calcular_bandejas_AAA(huevos_AAA):
    if huevos_AAA!=0:
        bandeja_AAA=huevos_AAA//12
    return bandeja_AAA
def calcular_bandejas_BC(huevos_BC):
    if huevos_BC!=0:
        bandeja_BC=huevos_BC//30
    return bandeja_BC

